package com.adrian.tema04.tema05;import java.time.LocalDateTime;import java.time.format.DateTimeFormatter;import java.util.Scanner;public class Ejercicio18 { public static void main(String[] args) {Scanner scanner = new Scanner(System.in);System.out.println("Ingresse su fecha de nacimiento (dd-mm-yyyy):");String fechaStr = scanner.nextLine();DateTimeFormatter formatter = DateTimeFormatter.ofPattern("dd-MM-yyyy");LocalDateTime fechaSalida = LocalDateTime.parse(fechaStr, formatter);String fechaNacimiento = String.valueOf(fechaSalida);int luckyNumber = calcularLuckyNumber(fechaNacimiento.trim());System.out.println("Tu nÃºmero de la suerte es: " + luckyNumber);scanner.close();}public static int calcularLuckyNumber(String fecha) {String soloDigitos = fecha.replace("-", "");int suma = sumarDigitos(soloDigitos);while (suma >= 10) {suma = sumarDigitos(String.valueOf(suma));}return suma;}public static int sumarDigitos(String cadena) {int suma = 0;for (char c : cadena.toCharArray()) {suma += Character.getNumericValue(c);}return suma;}}
